import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
    header: {
        overflow: 'hidden',
        float: 'left',
        padding: '12px',
        border: 'solid black',


    },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  fileInput: {
    width: '97%',
    margin: '10px 0',
  },
  buttonSubmit: {
    marginBottom: 10,
  },
}));
